import{_ as o,c as r,a as e,b as i,t,d as n,o as l}from"./app.15727171.js";const s="/assets/ios-text-to-speech-001.780a99b6.png",c="/assets/ios-text-to-speech-002.84774ee1.png",T=JSON.parse('{"title":"Texto a Voz","description":"","frontmatter":{"title":"Texto a Voz","editLink":true},"headers":[{"level":2,"title":"Haga que los artículos se lean en voz alta con Texto a Voz","slug":"haga-que-los-articulos-se-lean-en-voz-alta-con-texto-a-voz","link":"#haga-que-los-articulos-se-lean-en-voz-alta-con-texto-a-voz","children":[]},{"level":2,"title":"Descargr el audio para reproducción sin conexión","slug":"descargr-el-audio-para-reproduccion-sin-conexion","link":"#descargr-el-audio-para-reproduccion-sin-conexion","children":[]},{"level":2,"title":"Demostración","slug":"demostracion","link":"#demostracion","children":[]}],"relativePath":"es/using/text-to-speech.md","lastUpdated":1682580431000}'),d={name:"es/using/text-to-speech.md"},u={id:"frontmatter-title",tabindex:"-1"},p=e("a",{class:"header-anchor",href:"#frontmatter-title","aria-hidden":"true"},"#",-1),h=n('<p>Omnivore para iOS puede leer cualquier artículo o página web en voz alta, para que sus ojos puedan descansar un poco.</p><nav class="table-of-contents"><ul><li><a href="#haga-que-los-articulos-se-lean-en-voz-alta-con-texto-a-voz">Haga que los artículos se lean en voz alta con Texto a Voz</a></li><li><a href="#descargr-el-audio-para-reproduccion-sin-conexion">Descargr el audio para reproducción sin conexión</a></li><li><a href="#demostracion">Demostración</a></li></ul></nav><h2 id="haga-que-los-articulos-se-lean-en-voz-alta-con-texto-a-voz" tabindex="-1">Haga que los artículos se lean en voz alta con Texto a Voz <a class="header-anchor" href="#haga-que-los-articulos-se-lean-en-voz-alta-con-texto-a-voz" aria-hidden="true">#</a></h2><p>::: consejo Texto-a-Voz actualmente sólo está disponible en la aplicación Omnivore para iOS. :::</p><p>Así es como funciona:</p><ul><li>Guarde cualquier artículo en Omnivore.</li><li>Inicie la aplicación Omnivore y toque un elemento guardado en su Biblioteca para abrir el lector.</li><li>Toque el botón Escuchar (el icono de los auriculares en la parte superior) para iniciar la reproducción en modo de pantalla completa.</li></ul><p><img src="'+s+'" alt="Texto a Voz en Omnivore iOS"></p><p>Puede seguir el texto mientras se lee en voz alta. Deslice el dedo hacia la izquierda o hacia la derecha para saltar hacia atrás o hacia adelante.</p><ul><li>Toque la flecha hacia abajo o deslícese hacia abajo desde la parte superior para usar el modo de minireproductor.</li><li>Toque el icono (tres puntos) para acceder a controles de reproducción adicionales.</li></ul><h2 id="descargr-el-audio-para-reproduccion-sin-conexion" tabindex="-1">Descargr el audio para reproducción sin conexión <a class="header-anchor" href="#descargr-el-audio-para-reproduccion-sin-conexion" aria-hidden="true">#</a></h2><p>Desde la vista de la Biblioteca, mantenga presionado cualquier elemento y elija Descargar Audio. Esto descargará una versión sin conexión del artículo que se puede reproducir más tarde.</p><p><img src="'+c+'" alt="Descargar TTS sin conexión en iOS"></p><h2 id="demostracion" tabindex="-1">Demostración <a class="header-anchor" href="#demostracion" aria-hidden="true">#</a></h2>',13),m=e("iframe",{width:"560",height:"315",src:"https://www.youtube.com/embed/wyy1vC-e338",title:"YouTube video player",frameborder:"0",allow:"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share",allowfullscreen:""},null,-1);function g(a,x,_,v,f,z){return l(),r("div",null,[e("h1",u,[i(t(a.$frontmatter.title)+" ",1),p]),h,m])}const q=o(d,[["render",g]]);export{T as __pageData,q as default};
