import{_ as i,c as a,a as e,b as n,t as o,d,o as s}from"./app.8e8bab01.js";const v=JSON.parse('{"title":"RSS and Atom feeds","description":"","frontmatter":{"title":"RSS and Atom feeds","editLink":true},"headers":[{"level":2,"title":"Adding Feeds to Omnivore","slug":"adding-feeds-to-omnivore","link":"#adding-feeds-to-omnivore","children":[]},{"level":2,"title":"Organizing Feeds","slug":"organizing-feeds","link":"#organizing-feeds","children":[]}],"relativePath":"using/feeds.md","lastUpdated":1710695450000}'),r={name:"using/feeds.md"},l={id:"frontmatter-title",tabindex:"-1"},c=e("a",{class:"header-anchor",href:"#frontmatter-title","aria-hidden":"true"},"#",-1),h=d('<p>Omnivore supports subscribing to RSS (Really Simple Syndication) and Atom feeds.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Feeds are a beta feature, if you have questions or feedback please <a href="https://discord.gg/h2z5rppzz9" target="_blank" rel="noreferrer">join our Discord</a> and post them in our #feeds-beta channel.</p></div><nav class="table-of-contents"><ul><li><a href="#adding-feeds-to-omnivore">Adding Feeds to Omnivore</a></li><li><a href="#organizing-feeds">Organizing Feeds</a></li></ul></nav><h2 id="adding-feeds-to-omnivore" tabindex="-1">Adding Feeds to Omnivore <a class="header-anchor" href="#adding-feeds-to-omnivore" aria-hidden="true">#</a></h2><p>To add a feed to Omnivore navigate to the <a href="https://omnivore.app/settings/feeds" target="_blank" rel="noreferrer">feeds page</a> and click the <code>Add a feed</code> button in the top right corner and paste the URL to the feed in the <code>Add new Feed</code> dialog.</p><p>When a feed is first added, Omnivore will fetch the last 24hrs worth of items or at least one item if there are no items posted in the last 24hrs.</p><p>By default Omnivore will fetch the entire content of the feed item from the original URL (<code>Fetch link: Always</code>). You can change this and choose:</p><ul><li><code>Fetch link: Always</code>: Always fetch the entire content of the feed item from the original URL.</li><li><code>Fetch link: Never</code>: Only see the content shared as part of the feed.</li><li><code>Fetch link: When empty</code>: Only fetch the content from the original URL if the feed item has no content.</li></ul><h2 id="organizing-feeds" tabindex="-1">Organizing Feeds <a class="header-anchor" href="#organizing-feeds" aria-hidden="true">#</a></h2><p>Feeds will be displayed in the left menu of the Library in the subscriptions section, organized by their name. You can also view all your subscriptions on the <a href="https://omnivore.app/settings/subscriptions" target="_blank" rel="noreferrer">Subscriptions page</a>. This page has all your emails/newsletter and feed subscriptions, and can be used to unsubscribe from items.</p><p>Each feed item is tagged with the <code>RSS</code> label. So you can easily search for all your feed items by clicking on <code>Feeds</code> in the left menu under subscriptions or by doing a label search <code>label:RSS</code>.</p>',11);function f(t,p,g,m,u,b){return s(),a("div",null,[e("h1",l,[n(o(t.$frontmatter.title)+" ",1),c]),h])}const y=i(r,[["render",f]]);export{v as __pageData,y as default};
